import { Camera } from 'expo-camera';
import { useEffect, useState } from 'react';
import {
  ImageBackground,
  Keyboard,
  KeyboardAvoidingView,
  StyleSheet,
  View,
  TouchableWithoutFeedback,
  Text,
  TextInput,
  Pressable,
} from 'react-native';
import Toast from 'react-native-toast-message';
import * as MediaLibrary from 'expo-media-library';

import { Ionicons } from '@expo/vector-icons';
import { MaterialIcons } from '@expo/vector-icons';
import { SimpleLineIcons } from '@expo/vector-icons';

export default function CreatePostScreen() {
  const [name, setName] = useState('');
  const [location, setLocation] = useState('');
  const [focused, setFocused] = useState(null);
  const [photoUri, setPhotoUri] = useState('');

  const [hasPermission, setHasPermission] = useState(null);
  const [cameraRef, setCameraRef] = useState(null);
  const [type, setType] = useState(Camera.Constants.Type.back);

  useEffect(() => {
    (async () => {
      const { status } = await Camera.requestCameraPermissionsAsync();
      await MediaLibrary.requestPermissionsAsync();
      setHasPermission(status === 'granted');
    })();
  }, []);

  if (hasPermission === null) {
    return <View />;
  }

  if (hasPermission === false) {
    return Toast.show({
      type: 'error',
      text1: `Oops, you didn't grant permission to use the camera 😔`,
    });
  }

  const takePhoto = async () => {
    if (cameraRef) {
      const { uri } = await cameraRef.takePictureAsync();
      await MediaLibrary.createAssetAsync(uri);
      setPhotoUri(uri);
      console.log(photoUri);
    }
  };

  const pickImage = async () => {
    try {
      const { status } =
        await ImagePicker.requestMediaLibraryPermissionsAsync();

      if (status === 'granted') {
        const result = await ImagePicker.launchImageLibraryAsync({
          mediaTypes: ImagePicker.MediaTypeOptions.Images,
          allowsEditing: true,
          aspect: [1, 1],
          quality: 1,
        });

        if (!result.canceled) {
          const selectedAsset = await MediaLibrary.createAssetAsync(
            result.assets[0].uri
          );
          const { uri } = await MediaLibrary.getAssetInfoAsync(selectedAsset);
          setPhotoUri(uri);
        }
      }
    } catch (error) {
      console.log(error.message);
    }
  };

  return (
    <TouchableWithoutFeedback onPress={Keyboard.dismiss}>
      <KeyboardAvoidingView
        behavior={Platform.OS === 'ios' ? 'padding' : 'height'}
        style={{ flex: 1, justifyContent: 'center', zIndex: 10 }}
      >
        <View style={styles.main}>
          {photoUri ? (
            <ImageBackground
              style={styles.image}
              source={{
                uri: photoUri,
              }}
            ></ImageBackground>
          ) : (
            <Camera
              style={styles.image}
              type={type}
              ref={setCameraRef}
              ratio="1:1"
            >
              {/* <Pressable
                style={styles.flipContainer}
                onPress={() => {
                  setType(
                    type === Camera.Constants.Type.back
                      ? Camera.Constants.Type.front
                      : Camera.Constants.Type.back
                  );
                }}
              >
                <Text
                  style={{ fontSize: 18, marginBottom: 10, color: 'white' }}
                >
                  Flip
                </Text>
              </Pressable> */}
              <MaterialIcons
                name="flip-camera-ios"
                size={24}
                style={{
                  ...styles.flipContainer,
                  color: '#BDBDBD',
                  opacity: 0.8,
                }}
                onPress={() => {
                  setType(
                    type === Camera.Constants.Type.back
                      ? Camera.Constants.Type.front
                      : Camera.Constants.Type.back
                  );
                }}
              />
              <View style={styles.icon}>
                <MaterialIcons
                  name="photo-camera"
                  size={24}
                  style={{ color: '#BDBDBD', opacity: 0.8 }}
                  onPress={takePhoto}
                />
              </View>
            </Camera>
          )}

          {photoUri ? (
            <Pressable onPress={() => setPhotoUri(null)}>
              <Text style={styles.text}>Зробити нове фото</Text>
            </Pressable>
          ) : (
            <Pressable onPress={pickImage}>
              <Text style={styles.text}>Завантажити фото</Text>
            </Pressable>
          )}

          <TextInput
            style={
              focused === 'name'
                ? {
                    ...styles.input,
                    marginTop: 32,
                    borderBottomColor: '#FF6C00',
                  }
                : { ...styles.input, marginTop: 32 }
            }
            name="name"
            textContentType="name"
            placeholder="Назва..."
            placeholderTextColor="#BDBDBD"
            value={name}
            onChangeText={value => setName(value)}
            onFocus={() => setFocused('name')}
            onBlur={() => setFocused(null)}
          ></TextInput>

          <View style={styles.inputWrapper}>
            <SimpleLineIcons
              name="location-pin"
              size={24}
              style={styles.locationIcon}
            />
            <TextInput
              // style={{ ...styles.input, paddingLeft: 28 }}
              style={
                focused === 'location'
                  ? {
                      ...styles.input,
                      paddingLeft: 28,
                      borderBottomColor: '#FF6C00',
                    }
                  : { ...styles.input, paddingLeft: 28 }
              }
              name="location"
              textContentType="location"
              placeholder="Місцевість..."
              placeholderTextColor="#BDBDBD"
              value={location}
              onChangeText={value => setLocation(value)}
              onFocus={() => setFocused('location')}
              onBlur={() => setFocused(null)}
            ></TextInput>
          </View>

          <Pressable
            style={{
              ...styles.button,
              backgroundColor: name && location ? '#FF6C00' : '#F6F6F6',
            }}
          >
            <Text
              style={{
                ...styles.btnText,
                color: name && location ? '#FFFFFF' : '#BDBDBD',
              }}
            >
              Опубліковати
            </Text>
          </Pressable>

          <View style={styles.deleteIcon}>
            <Ionicons name="ios-trash-outline" size={24} color="#BDBDBD" />
          </View>
        </View>
      </KeyboardAvoidingView>
    </TouchableWithoutFeedback>
  );
}

const styles = StyleSheet.create({
  main: {
    flex: 1,
    paddingTop: 32,
    paddingHorizontal: 16,
    paddingBottom: 34,
    backgroundColor: '#FFFFFF',
    zIndex: 10,
  },

  image: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    overflow: 'hidden',

    backgroundColor: '#F6F6F6',
    borderColor: '#E8E8E8',
    borderStyle: 'solid',
    borderWidth: 1,
    height: 240,
    borderRadius: 8,
  },

  flipContainer: {
    position: 'absolute',
    top: 16,
    left: 16,
  },

  icon: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',

    height: 60,
    width: 60,
    borderRadius: 30,
    borderColor: '#BDBDBD',
    borderStyle: 'solid',
    borderWidth: 1,
    backgroundColor: 'inherit',
    opacity: 0.8,
  },

  text: {
    fontFamily: 'Roboto',
    fontWeight: 400,
    fontSize: 16,
    lineHeight: 19,
    color: '#BDBDBD',
    marginTop: 8,
  },

  input: {
    height: 50,

    borderBottomColor: '#E8E8E8',
    borderBottomWidth: 1,

    fontFamily: 'Roboto',
    fontWeight: '400',
    fontSize: 16,
    lineHeight: 19,
    color: '#212121',
  },

  inputWrapper: {
    marginTop: 16,
    marginBottom: 32,
  },

  locationIcon: {
    position: 'absolute',
    top: 10,
    left: 0,
    color: '#BDBDBD',
    opacity: 0.5,
  },

  button: {
    padding: 16,
    marginBottom: 16,
    borderRadius: 100,
    backgroundColor: '#F6F6F6',
  },

  btnText: {
    fontFamily: 'Roboto',
    fontWeight: '400',
    fontSize: 16,
    lineHeight: 19,
    textAlign: 'center',
    color: '#BDBDBD',
  },

  deleteIcon: {
    width: 70,
    borderRadius: 20,
    backgroundColor: '#F6F6F6',

    paddingHorizontal: 23,
    paddingVertical: 8,

    marginTop: 'auto',
    marginLeft: 'auto',
    marginRight: 'auto',
  },
});

// imp

export const useKeyboardListenerWithOpen = number => {
  const [keyboardHeight, setKeyboardHeight] = useState(0);
  const [keyboardOpen, setKeyboardOpen] = useState(false);

  useEffect(() => {
    const keyboardDidShowListener = Keyboard.addListener(
      'keyboardDidShow',
      event => {
        const { height } = event.endCoordinates;
        setKeyboardHeight(height - number);
        setKeyboardOpen(true);
      }
    );

    const keyboardDidHideListener = Keyboard.addListener(
      'keyboardDidHide',
      () => {
        setKeyboardHeight(0);
        setKeyboardOpen(false);
      }
    );

    return () => {
      keyboardDidShowListener.remove();
      keyboardDidHideListener.remove();
    };
  }, []);
  return { keyboardHeight, keyboardOpen };
};

export const useKeyboardListener = number => {
  const [keyboardHeight, setKeyboardHeight] = useState(0);

  useEffect(() => {
    const keyboardDidShowListener = Keyboard.addListener(
      'keyboardDidShow',
      event => {
        const { height } = event.endCoordinates;
        setKeyboardHeight(height - number);
      }
    );

    const keyboardDidHideListener = Keyboard.addListener(
      'keyboardDidHide',
      () => {
        setKeyboardHeight(0);
      }
    );

    return () => {
      keyboardDidShowListener.remove();
      keyboardDidHideListener.remove();
    };
  }, []);
  return { keyboardHeight };
};

export const usePasswordVisibility = (initialState, password) => {
  const [showPassword, setShowPassword] = useState(initialState);
  const [hidden, setHidden] = useState('#F6F6F6');

  const togglePasswordVisibility = () => {
    setShowPassword(!showPassword);
  };

  useEffect(() => {
    if (password === '') {
      setHidden('#F6F6F6');
    } else {
      setHidden('#1B4371');
    }
  }, [password]);

  return { showPassword, hidden, togglePasswordVisibility };
};
